const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  const Header = (props) => {
    return (
      <h1>
        {props.course}
      </h1>
    )
  }

  const Content = (props) => {
    return (
      <div>
      <Part part = {props.parts[0]}/>
      <Part part = {props[1]}/>
      <Part part = {props[2]}/>
      </div>
    )
  }

  const Part = (props) => {
    return(
    <p>
        {props.name} {props.exercises}
      </p>
    )
  }

  const Total = (props) => {
    return (
      <p>Number of exercises {props.total1 + props.total2 + props.total3}</p>
    )
  }

  return (
    <div>
      <Header course={course}/>
      <Content parts ={parts}/>
      <Total total1={parts[0].exercises} total2={parts[1].exercises} total3={parts[2].exercises}/>
    </div>
  )
}

export default App
