const Course = ({course}) => {
  const total = course.map((x)=>{x.parts}).reduce((sum,x) => 
    sum + x.exercises, 0)
    return (
      course.map((x)=>
      <>
      <h1 key = {x.id}>{x.name}</h1>
        {x.parts.map((x) => 
          <li key ={x.id}>
            {x.name} {x.exercises}
            </li>
        )}
      <div>total of {total} exercises</div>
      </>)
    )
  }




  export default Course
